import {React, useState }from 'react';
import { AppBar, Toolbar, Typography } from '@mui/material'; // immer @mui zum importieren nehmen kp warum 
import { makeStyles} from "@mui/styles"
import { Search } from "@mui/icons-material"
import { InputBase } from '@mui/material';
import { alpha } from '@mui/material';
import { Badge } from '@mui/material';
import { Mail, Notifications } from '@mui/icons-material';
import { Avatar } from '@mui/material';





const useStyles = makeStyles((theme)=>( {

    toolbar: {
        display: "flex",
        justifyContent:"space-between"
    },
    logoLg: { 
        display: "none",
        [theme.breakpoints.up("sm")]: {
        display: "block",
        },
    },
    logoSm:{
        display: "block",
        [theme.breakpoints.up("sm")]: {
        display: "none",
    },
    },
    search: {
        display: "flex",
        alignItems: "center",
        backgroundColor: alpha(theme.palette.common.white, 0.15),
        '&:hover': {
            backgroundColor: alpha(theme.palette.common.white, 0.25),
          },
          borderRadius: theme.shape.borderRadius,
        width: "50%",
        [theme.breakpoints.down("sm")]: {
            display: (props)=> (props.open ? "flex" : "none"),
        },
    },
    searchButton: {
        [theme.breakpoints.down("sm")]: {
            display: (props)=> (props.open ? "flex" : "none"),
        },
    },


    
    input: {
        color: "white",
        marginLeft: theme.spacing(1),
        },

    icons:{
        
        alignItems: "center",
        margin: "10px",
        display: (props)=> (props.open ? "flex" : "none"),
    },
    badge: {
        marginRight: theme.spacing(2),
    }
  

}));


export default function Navbar() {
    const [open, setOpen] = useState(false);  // Startbedingung (Konstruktor)
    const classes = useStyles({open});    

    return (
   
            <AppBar>
                <Toolbar className = {classes.toolbar}>
                    <Typography variant="h6" className={classes.logoLg} >
                        Modern Timber En
                    </Typography>
                    <Typography variant="h6" className={classes.logoSm} >
                        MTE
                    </Typography>

                    <div className ={classes.search}>
                        <Search/>
                        <InputBase placeholder ="such such such...." className={classes.input}/>
                    </div>
                    <div className={classes.icons}>
                        <Search className={classes.searchButton} 
                        onClick={()=>setOpen(true)} 
                        />
                        <Badge badgeContent={4} color="secondary" className={classes.badge} >
                        <Mail/>
                        </Badge>
                        <Badge badgeContent={2} color="secondary"className={classes.badge}>
                        <Notifications/>
                        </Badge>
                        <Avatar alt="Remy Sharp" src="https://image.stern.de/8846008/t/YV/v2/w960/r1.7778/-/baum.jpg"/>

                        
                    </div>

               
                </Toolbar>
            </AppBar>
    
    );
};
